/**
 * Mapping of state map abbreviations to cohort information
 *
 * Thought this would help since some of the counts in the tooltips were wrong
 * Also you instantiate 35+ tooltips when it could just be one
 * And you can even add the SVG code in here
 * Hovering over Kansas shows the Maine locations
 * Also the dots for some states like California or Washington don't line up with the locations count
 */

export const mapSettings = {
  cohortRadius: 3,
  totalWidth: 959,
  totalHeight: 593,
  hoverDelay: 600,
  clickDelay: 50,
  leaveDelay: 2000
};

type Circle = {
  x: number;
  y: number;
  r: number;
};

type Path = {
  d: string;
};

type IsCircle = Circle & {
  type: "circle"
  d?: never;
}

type IsPath = Path & {
  type: "path"
  x?: never;
  y?: never;
  r?: never;
}

export type CohortMember = Pick<Circle, "x" | "y"> & {
  agency: string;
};

export type State = {
  state: string;
  members?: CohortMember[];
  drawable: IsCircle | IsPath;
};

